    private void CompressButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CompressButtonActionPerformed
        //Variables*************************************************************
        int Data [] [];
        Data = new int [26][2];
        int next=0;
        String input = TextField.getText();
        int z=0;
        //Loading Dictionary****************************************************
        for(int e=65;e<=90;e++)
        {              
            Data[z][0]=e; 
            z++;
        }
        //Letters Counter*******************************************************
         boolean isfound = true;
         for(int i=0;i<input.length();i++)
         {
             for(int j=0;j<26;j++)
             {
                // System.out.println((char)Data[j][0]+ "-" +input.charAt(i));
                 if(Data[j][0]==(int)input.charAt(i))
                 {                     
                     Data[j][1]++;                     
                 }
             }            
         }
         //Diciotnary Check*****************************************************
         /*for(int y=0;y<26;y++)
         {
             System.out.println((char)Data[y][0] + "-" + Data[y][1]);
         }*/
         //Huffman Algorithem***************************************************
         Node constr = new Node();
         PriorityQueue <Node> TreeB = new PriorityQueue<Node>();
         for(int y=0;y<26;y++)
         {
             if(Data[y][1]>0)
             {
                TreeB.offer(new Node((char)Data[y][0],Data[y][1]));
             }
         }
         while(TreeB.size()>2)         
         {
            Node left = TreeB.poll();
            Node right = TreeB.poll();
            Node mn = new Node();
            mn = mn.MergeNode(left, right);
            //constr.NodePrinter(mn);            
            TreeB.offer(mn);            
         }
         
         constr.settingNodes(TreeB);         
         KeyLetter=constr.CDictionarySetter(KeyLetter);
         KeyCode=constr.DDictionarySetter(KeyCode);
         String EncodedData = "";
         for(int i=0;i<input.length();i++)
         {
             EncodedData+=KeyLetter.get(input.charAt(i));
         }
         DataTags.setText(constr.DataPrinter());
         Results.setText(EncodedData);         
         
    }//GEN-LAST:event_CompressButtonActionPerformed

    private void DecompressButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_DecompressButtonActionPerformed
        String Data = Results.getText();
        String find="";
        String DResult ="";
        for (int i = 0; i <Data.length();i++)
        {
            find +=  Data.charAt(i);
            if(KeyCode.containsKey(find)==true)
            {
                DResult+=KeyCode.get(find);
                find="";
            }   
        }
        Results.setText(DResult);
        
    }//GEN-LAST:event_DecompressButtonActionPerformed

